CREATE SCHEMA `zoo` ;

CREATE TABLE `zoo`.`animals` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(45) NULL,
  `scientific_name` VARCHAR(45) NULL,
  `life_span` INT NULL,
  `weight_lb` DECIMAL(10,2) NULL,
  `diet` VARCHAR(45) NULL,
  `endangered_status` VARCHAR(45) NULL,
  `picture` VARCHAR(170) NULL,
  PRIMARY KEY (`id`),
  UNIQUE INDEX `name_UNIQUE` (`name` ASC) VISIBLE,
  UNIQUE INDEX `animalscol_UNIQUE` (`scientific_name` ASC) VISIBLE);

-- All this info is from NC Zoo Link: https://www.nczoo.org/wildlife/animals
